def add_numbers(num1,num2):
    return num1+num2
def subtract_numbers(num1,num2):
    return num1-num2
def multiply_numbers(num1,num2):
    return num1*num2
def divide_numbers(num1,num2):
    if num2!=0:
        return num1/num2
    else:
        raise Exception
def modulus(num1,num2):
    return num1%num2
def power(num1,num2):
    return num1**num2

