ckan.module("stats-nav",function($){return{options:{prefix:"stats-"},initialize:function(){var location=this.sandbox.location;var prefix=this.options.prefix;var hash=location.hash.slice(1);var selected=this.el.find('[href^="#'+prefix+hash+'"]');this.el.on("shown.bs.tab",function(event){location.hash=event.target.hash.slice(prefix.length+1);});if(selected.length){selected.tab("show");}}};});