#!/usr/bin/env python
# setup.py generated by flit for tools that don't yet use PEP 517

from distutils.core import setup

extras_require = \
{":python_version < '3'": ['pip~=20.3'],
 ":python_version == '3.3.*'": ['pip~=10.0'],
 ":python_version == '3.4.*'": ['pip~=19.1.1'],
 ":python_version == '3.5.*'": ['pip~=20.3'],
 ":python_version > '3.5'": ['pip>=21']}

setup(name='pip-with-requires-python',
      version='1.0.1',
      description='install pip with Requires-Python support',
      author='Thomas Grainger',
      author_email='pip-with-requires-python@graingert.co.uk',
      url='https://github.com/graingert/pip-with-requires-python',
      py_modules=['pip_with_requires_python'],
      extras_require=extras_require,
     )
