def uint(v: str):
    return f'uint(v: {v})'


def flux_int(v: str):
    return f'int(v: {v})'


def flux_time(v):
    return f'time(v: {v})'


def flux_bool(v):
    return f'bool(v: {v})'


def flux_float(v):
    return f'float(v: {v})'


def flux_bytes(v):
    return f'bytes(v: {v})'


def flux_string(v):
    return f'string(v: {v})'
